// example code, renders a basic rainbow
var tv = {
	hue: 0,
	xPos: 0,
	yPos: 0
};

var imageData = 'data:image/png;base64,';
var image = null;

return {
    
	init: function(display,timeline) {
		this.display = display;

        //debugger;
        image = display.loadImage(imageData);
        
        tv.xPos = -image.width;
        tv.yPos = display.rows-image.height;
      	display.tween(tv,{
          to: {xPos: display.cols},
          duration: 5000,
          repeat: Infinity,
          yoyo: false,
          delay: 500
        });
	},
	onUpdate: function(display, data) {
		var c = null;
		display.clear(new Color(0x00));
		display.drawImage(image, Math.round(tv.xPos),Math.round(tv.yPos));
	}
};
