// example code, renders a basic rainbow
var tweenedValues = {
	hue: 0
};

return {
	init: function(display,timeline) {
		this.display = display;
       // debugger;
      	display.tween(tweenedValues,{
          to: {hue: 256},
          duration: 5000,
          repeat: Infinity,
          yoyo: false,
          delay: 0
        });
        this.initPalette(display);
        this.initPlasma(display);
	},
	initPalette: function(display) {
	  this.palette = [];
	  for (var i=0; i < 256; i++) {
	      var c = new Color();
	      c.setRGB(
	          (128.0 + 128 * Math.sin(3.1415 * i / 32)) / 255.0,
	          (128.0 + 128 * Math.sin(3.1415 * i / 64)) / 255.0,
	          (128.0 + 128 * Math.sin(3.1415 * i / 128.0)) / 255.0
	      );
	      //c.setHSL(i/255, 1.0, 0.5);
	      this.palette.push(c);
	  }
	},
	initPlasma: function(display) {
	   this.plasma = [];
	   for (var x=0; x < display.cols; x++) {
	       var row = [];
	       for (var y=0; y < display.rows; y++) {
	           row.push(
	               Math.round(
	                    128 + (128 * Math.sin(x / 16)) +
	                    128 + (128 * Math.sin(y / 8)) +
	                    128 + (128 * Math.sin((x+y) / 16)) +
	                    128 + (128 * Math.sin(Math.sqrt(x*x+y*y)) / 8)
	           ));
	       }
	       this.plasma.push(row);
	   }
	},
	onUpdate: function(display, data) {
		var c = null;

		for (var i=0; i < display.leds.length; i++) {
			var x = i%display.cols,
				y = Math.floor(i/display.cols);
				
			c = this.palette[((this.plasma[x][y]) + Math.round(tweenedValues.hue)) % 255];
			display.setColor(i,c);
		}
	}
};
