// example code, renders a basic rainbow
var tv = {
	hue: 0,
	xPos: 0,
	yPos: 0
};

var imageData = 'data:image/png;base64,';
var image = null;

return {

	init: function(display,timeline) {
		this.display = display;

        //debugger;
        image = display.loadImage(imageData);

        tv.xPos = -image.width;
      	display.tween(tv,{
          to: {xPos: display.cols},
          duration: 22000,
          repeat: Infinity,
          yoyo: true,
          delay: 0,
          easing: TWEEN.Easing.Linear.None
        });
 	    display.tween(tv,{
          to: {hue: 1},
          duration: 3000,
          repeat: Infinity,
          yoyo: true,
          delay: 500,
          easing: TWEEN.Easing.Cubic.InOut
        });        
	},
	onUpdate: function(display, data) {
        var c = new Color(0x0);
		var h = (tv.hue),
			s = 1,
			l = 0.5;
			c.setHSL(h,s,l);
		display.clear(c);
		display.drawImage(image, Math.round(tv.xPos),0, new Color(0x00));
	}
};
