// example code, renders a basic rainbow
var tv = {
	hue: 0,
	xPos: 0,
	yPos: 0,
	frame: 0
};

var imageData = 'data:image/png;base64,';

var image = null;

var tileMap = {
    tileWidth: 19,
    tileHeight: 10,
    tileBorder: 1
}

return {
    
	init: function(display,timeline) {
		this.display = display;
    
        //debugger;
        
        tv.xPos = -tileMap.tileWidth;
      	display.tween(tv,{
          to: {frame: 7},
          duration: 500,
          repeat: Infinity,
          yoyo: false,
          delay: 0
        });
      	display.tween(tv,{
          to: {xPos: display.cols},
          duration: 5000,
          repeat: Infinity,
          yoyo: false,
          delay: 0
        });
        
        image = display.loadImage(imageData);
	},

	onUpdate: function(display, data) {
		var c = null;
		display.clear(new Color(0x00));
		//display.drawImageRegion(image, 3,3, 5,0, 8, 11, new Color(0x00));
		var frame = Math.round(tv.frame);
		display.drawImageTile(image, Math.round(tv.xPos),display.rows-tileMap.tileHeight, frame,0, tileMap, new Color(0x00));
	}
};
