// example code, renders a basic rainbow
var tv = {
	hue: 0,
	xPos: 0,
	yPos: 0,
	frame: 0
};


var displayString = "Hello world\nSupplyframe HQ!";


return {
    
	init: function(display,timeline) {
		this.display = display;
    //debugger;
        tv.xPos = display.cols;
      	display.tween(tv,{
          to: {xPos: -displayString.length*display.getFont('default').map.tileWidth},
          duration: 5000,
          repeat: Infinity,
          yoyo: false,
          delay: 0
        });
        
	},

	onUpdate: function(display, data) {
		var c = null;
		display.clear(new Color(0x00));
		var fontHeight = display.getFont('default').map.tileHeight;
		display.drawString(displayString,Math.round(tv.xPos),fontHeight,[new Color(0xFF0000), new Color(0x00FF00), new Color(0x0000FF)]);
	}
};
