var tv = {
    zoom: 0,
    rotate: 0
}
return {
    createLookupTable: function(width,height, textureSize) {
        this.table = [];
        for (var y=0; y < height; y++) {
            this.table[y] = [];
            for (var x=0; x < width; x++) {
                var relativeX = x - width / 2,
                    relativeY = height / 2 - y; // y - height / 2;
                    
                var angle = 0;
                    
                if (relativeY === 0) {
                    if (relativeX > 0) {
                        angle = -Math.PI/2;
                    } else {
                        angle = Math.PI/2;
                    }
                } else {
                    angle = Math.atan(relativeX/relativeY);
                }
                
                if (relativeY > 0) {
                   angle = angle + Math.PI;
                }
                angle += Math.PI/2;
                angle = angle * 1;
                this.table[y][x] = { angle : angle, translated: angle / (1*Math.PI) * textureSize };
            }
        }
    },
    createDepthTable:function(width,height,textureSize) {
          
        this.depthTable = [];
        for (var y=0; y < height; y++) {
            this.depthTable[y] = [];
            for (var x=0; x < width; x++) {
                var relativeX = x - width / 2,
                    relativeY = height / 2 - y;
                    
                var depth = Math.pow(2,10) / (relativeX*relativeX + relativeY*relativeY);
                this.depthTable[y][x] = depth;
            }
        }
    },
    init: function(display,timeline) {
        this.display = display;
        
       // debugger;
        this.textureImage = display.loadImage(texture);
        this.createLookupTable(display.cols,display.rows,this.textureImage.width);
        this.createDepthTable(display.cols,display.rows,this.textureImage.width);
        
      	display.tween(tv,{
          to: {zoom: 32},
          duration: 15000,
          repeat: Infinity,
          yoyo: false,
          delay: 0,
       //   easing: TWEEN.Easing.Cubic.InOut
        });
      	display.tween(tv,{
          to: {rotate: this.textureImage.width},
          duration: 12000,
          repeat: Infinity,
          yoyo: false,
          delay: 0,
          //easing: TWEEN.Easing.Cubic.InOut
        });
    },
    
    getImagePixel: function(image, x,y) {
        var imageIndex = (x*4) + ((y)*image.width*4);
        var c = new Color();
                c.setRGB(image.data[imageIndex]/255,image.data[imageIndex+1]/255,image.data[imageIndex+2]/255);
        return c;
    },
    onUpdate: function(display, data) {
        var c = null;

        for (var x = 0; x < display.cols; x++) {
            for (var y=0; y < display.rows; y++) {
            
                var tmpX = x, tmpY = y;
                /*
                c = new Color(0x0);
                var h = 0,
                    s = 1,
                    //l = (this.depthTable[y][x])%1;
                    l = (this.table[y][x].translated/textureSize)%1;
                c.setHSL(h,s,l);
                var index = y*display.cols + x;*/
                
                
                var texX = Math.round(this.table[y][x].translated + tv.rotate),
                    texY = Math.round(this.depthTable[y][x] + tv.zoom);
                    
                texX = texX % this.textureImage.width;
                texY = texY % this.textureImage.height;
                    
                while (tmpX < 0) {
                    tmpX = tmpX + this.textureImage.width;
                }
                while (tmpY < 0) {
                    tmpY = tmpY + this.textureImage.height;
                }
                while (tmpX > this.textureImage.height) {
                    tmpX = tmpX - this.textureImage.width;
                }
                while (tmpY > this.textureImage.height) {
                    tmpY = tmpY - this.textureImage.height;
                }
                c = this.getImagePixel(this.textureImage, texX,texY);
                
                c.multiplyScalar(1 - Math.min((this.depthTable[y][x]/32),1) );
                var index = tmpY*display.cols + tmpX;
                display.setColor(index, c);
            }
        }
    }
}
